package leetcode.april.easy;

/**
 * https://leetcode.com/problems/find-the-difference/
 */
public class FindTheDifference {

    public char findTheDifference(String s, String t) {
        int[] alphabets = new int[26];
        for (int i = 0; i < s.length(); i++) alphabets[s.charAt(i) - 97] += 1;
        for (int i = 0; i < t.length(); i++) alphabets[t.charAt(i) - 97] -= 1;
        for (int i = 0; i < alphabets.length; i++) if (alphabets[i] == -1) return (char) (i + 97);
        return 'a';
    }

    public static void main(String[] args) {
        FindTheDifference findTheDifference = new FindTheDifference();
        System.out.println(findTheDifference.findTheDifference(
                "",
                ""        ));
    }
}
