package leetcode.medium;

import java.util.HashMap;
import java.util.Map;

/**
 * https://leetcode.com/problems/number-of-substrings-containing-all-three-characters/
 */
public class SlidingWindowNumberOfSubstringsContainingAllThreeCharacters {

    public int numberOfSubstrings(String s) {
        int last[] = {-1, -1, -1}, res = 0, n = s.length();
        for (int i = 0; i < n; ++i) {
            last[s.charAt(i) - 'a'] = i;
            res += 1 + Math.min(last[0], Math.min(last[1], last[2]));
        }
        return res;
    }

    public int numberOfSubstrings2(String s) {
        int i=0, j=1;
        int sum = 0;
        while(j<=s.length()) {
            String substring = s.substring(i, j);
            int aIndex = substring.lastIndexOf("a");
            int bIndex = substring.lastIndexOf("b");
            int cIndex = substring.lastIndexOf("c");
            if(aIndex>-1 && bIndex>-1 && cIndex>-1) {
                if(aIndex>=1 && bIndex>=1 && cIndex>=1) {
                    sum += s.length() - j + 1;
                    i++;
                } else {
                    sum += s.length() - j + 1;
                    i++;
                    j++;
                }
            } else {
                j++;
            }
        }
        return sum;
    }

    public static void main(String[] args) {
        SlidingWindowNumberOfSubstringsContainingAllThreeCharacters numberOfSubstringsContainingAllThreeCharacters  = new SlidingWindowNumberOfSubstringsContainingAllThreeCharacters();
        System.out.println(numberOfSubstringsContainingAllThreeCharacters.numberOfSubstrings(""));
    }
}
